# settingManager.pyimport configimport globalVarsclass SettingManager:    def __init__(self):        self.configSpec = {            "autoLoadDictionary": "boolean(default=True)",            "maxSuggestions": "integer(default=10)",             "enableSound": "boolean(default=True)"        }        config.conf.spec["siamSquare"] = self.configSpec    def getSetting(self, settingName):        try:            return config.conf["siamSquare"][settingName]        except KeyError:            return self.getDefaultSetting(settingName)    def setSetting(self, settingName, value):        try:            config.conf["siamSquare"][settingName] = value        except KeyError:            config.conf["siamSquare"] = {}            config.conf["siamSquare"][settingName] = value    def getDefaultSetting(self, settingName):        defaults = {            "autoLoadDictionary": True,            "maxSuggestions": 10,            "enableSound": True        }        return defaults.get(settingName)